def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**0.5)+1):
        if n % i == 0:
            return False
    return True

def factorial_loop(n):
    result = 1
    for i in range(2, n+1):
        result *= i
    return result

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def power(x, y):
    result = 1
    for _ in range(y):
        result *= x
    return result
